import React from 'react'
import { useState } from 'react'
import AppSwitch from'./AppSwitch'
import UploadFile from './UploadFile'
import AppTableModal from './AppTableModal'
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import Fab from '@mui/material/Fab';
import InfoRoundedIcon from '@mui/icons-material/InfoRounded';

const Text = "Text"
const Features = "Features"

const firstColor = "#ffcc80"
const secondColor = "#80cbc4"

const HandleFile = ({handleFileSubmit}) => {
    const [themeColor, setThemeColor] = useState(firstColor)  
    const [classifyingType, setClassifyingType] = useState(Text)
    const [showTextModalTable, setShowTextModalTable] = React.useState(false)
    
    const handleSwitch = (SwitchText) => {
        setClassifyingType(SwitchText)
        if(themeColor === firstColor)
          setThemeColor(secondColor)
        else
          setThemeColor(firstColor)
        console.log(SwitchText)
      }

    return (
        <Container maxWidth='md' sx={{width: "90%"}}>
            <p style={{fontFamily: "Calibri light", fontSize: "1.5rem", color: "#bdbdbd", paddingLeft: "0.5rem", textDecoration: "underline"}}>
                Choose Classifyier Type
            </p>
            <AppSwitch 
                leftSwitchText={Text}
                rightSwitchText={Features}
                onSwitch={handleSwitch}
                firstColor={firstColor}
                secondColor={secondColor}
            />
            
            <div style={{color: themeColor}}>
                <hr/>
                {classifyingType === Features?
                <>
                Upload Excel file in the following format:
                (features (numbers) : class (numbers/strings))
                </>
                :
                <>
                
                Upload Excel file in the following format:
                (text () : class)
                <br/>

                <Fab 
                style={{color: "inherit"}}
                onClick={() => setShowTextModalTable(true)}
                size="small" 
                variant="extended" color={themeColor}>
                <InfoRoundedIcon sx={{ mr: 1 }} />
                Show Example
                </Fab>


                <AppTableModal 
                    show={showTextModalTable}
                    onHide={() => setShowTextModalTable(false)} 
                    title={"File upload example for text classification"}
                    features={["Text", "Class"]} 
                    items={[["text1", "class1"]]}
                />
                </>
                }   
                <hr/>
                <UploadFile 
                classifyingType={classifyingType}
                onSubmit={handleFileSubmit}
                />
            </div>
        </Container>
    )
}

export default HandleFile
