import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';

const HandleTextDataToPredict = ({onSubmit}) => {
    const [textValue, setTextValue] = useState(null)
    const [textValueMessage, setTextValueMessage] = useState("")
    const [textValueError, setTextValueError] = useState(false)

    const [classValue, setClassValue] = useState(null)
    const [classValueMessage, setClassValueMessage] = useState("")
    const [classValueError, setClassValueError] = useState("")

    const handleSubmit = () => {
        if(classValue === null || classValue === "")
        {
            console.log(classValue)
            setClassValueError(true)
            setClassValueMessage("Empty Class")
            return
        }
        if(textValue === null || textValue === "")
        {
            console.log(textValue)
            setTextValueError(true)
            setClassValueMessage("Empty Text")
            return
        }
    }

    const handleTextChange = e => {
        setTextValue(e.target.value)
    }

    const handleClassChange = e => {
        setClassValue(e.target.value)
    }

    return (
        //maxWidth='md' sx={{width: "90%"}}
        <Container> 
            <TextField
                error = {textValueError}
                multiline
                rows={3}
                fullWidth 
                label="Text"
                helperText={textValueMessage}
                onChange={handleTextChange}
            />
            <Button onClick={handleSubmit}>click</Button>
        </Container>
    );
}

export default HandleTextDataToPredict
