import * as React from "react";
import { styled } from "@mui/material/styles";
import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Switch from "@mui/material/Switch";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";
import Container from '@mui/material/Container';


const AppSwitch = ({leftSwitchText, rightSwitchText, onSwitch, firstColor, secondColor}) => {
  const [switchOn, setSwitchOn] = React.useState(false) //init as unchecked


  const handleSwitchClick = () => {
    //console.log("Before switch:", switchOn)
    setSwitchOn(!switchOn)
    if(!switchOn)
      onSwitch(rightSwitchText)
    else
      onSwitch(leftSwitchText)
    //console.log("After switch:", switchOn)
  }

    return (
        <div>
          <Container>
            <FormGroup>
              <Stack direction="row" spacing={1} alignItems="center">
                <Typography sx={{color: firstColor}}>{leftSwitchText}</Typography>
                <FormControlLabel
                  onClick={handleSwitchClick}
                  control=
                  {
                  <MySwitch 
                    sx={{ m: 1, textAlign: "center"}} 
                    firstColor={firstColor} 
                    secondColor={secondColor}
                  />
                  }
                  label=""
                />
                <Typography sx={{color: secondColor}}>{rightSwitchText}</Typography>
              </Stack>
            </FormGroup>
          </Container>
        </div>
      );
}

const MySwitch = styled(() => (
  <Switch focusVisibleClassName=".Mui-focusVisible" disableRipple />))(({ theme }) => ({
  width: 42,
  height: 26,
  padding: 0,
  "& .MuiSwitch-switchBase": {
    padding: 0,
    margin: 2,
    transitionDuration: "300ms",
    "&.Mui-checked": {
      transform: "translateX(16px)",
      color: "#fff",
      "& + .MuiSwitch-track": {
        backgroundColor: "black",
        opacity: 1,
        border: 0
      },
      "&.Mui-disabled + .MuiSwitch-track": {
        opacity: 0.5
      }
    },
    "&.Mui-focusVisible .MuiSwitch-thumb": {
      color: "#000",
      border: "6px solid #fff"
    },
    "&.Mui-disabled .MuiSwitch-thumb": {
      color:
        theme.palette.mode === "light"
          ? theme.palette.grey[100]
          : theme.palette.grey[600]
    },
    "&.Mui-disabled + .MuiSwitch-track": {
      opacity: theme.palette.mode === "light" ? 0.7 : 0.3
    }
  },
  "& .MuiSwitch-thumb": {
    boxSizing: "border-box",
    width: 22,
    height: 22,
  },
  "& .MuiSwitch-track": {
    borderRadius: 26 / 2,
    backgroundColor: "black",
    opacity: 1,
    transition: theme.transitions.create(["background-color"], {
      duration: 500
    })
  }
}));

export default AppSwitch;
