import React, { useState } from 'react';
import axios from 'axios';
import Alert from 'react-bootstrap/Alert'
import ProgressBar from 'react-bootstrap/ProgressBar'
import Spinner from 'react-bootstrap/Spinner'
import Button from 'react-bootstrap/Button'
import LinearProgress from '@mui/material/LinearProgress'
import Fab from '@mui/material/Fab';

const UploadFile = ({show, onSubmit}) => {
  const [file, setFile] = useState('');
  const [filename, setFilename] = useState('Upload Excel file');
  const [message, setMessage] = useState('');
  const [messageVariant, setMessageVariant] = useState('');
  const [uploadPercentage, setUploadPercentage] = useState(0);
  const [showSpinner, setShowSpinner] = useState(false)

  const onChange = e => {
    setFile(e.target.files[0]);
    setFilename(e.target.files[0].name);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    setShowSpinner(true)

    var fileUploadSuccess = true
    try {
      const res = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: progressEvent => {
          setUploadPercentage(parseInt(Math.round((progressEvent.loaded * 100) / progressEvent.total)));
        }
      });
      setTimeout(() => setUploadPercentage(0),3000);
      setTimeout(() => setMessage(''), 3000);

      setMessage('קורסים נטענו בהצלחה')
      setMessageVariant('success')
    } 
    catch (err) 
    {
      fileUploadSuccess = false
      if (err.response.status === 500)
      {
        setMessage('Server Error', err.response)
        setMessageVariant('danger')
      }
      else if (err.response.status === 400)
      {
        setMessage('יש להעלות קובץ PDF')
        setMessageVariant('warning')
      }
      else 
      {
        setMessage('שגיאה');
        setMessageVariant('info')
      }
      setUploadPercentage(0)
    }
    finally{
      setShowSpinner(false)
      if(fileUploadSuccess){
        onSubmit() //to outside scope
      }
    }
  };

  return (
    <>
        <div className='mx-auto' style={uploadStyle}>
            {message ? <Alert variant={messageVariant} >{message}</Alert> : null}
            {uploadPercentage === 100 ? <Alert variant='info' >הקובץ הועלה, קורסים נטענים...</Alert> : null}

            <form onSubmit={onSubmit} style={{textAlign:'center'}} >
            <div className='custom-file mb-'>
                <input
                type='file'
                className='custom-file-input'
                size="lg"
                id='customFile'
                onChange={onChange}
                />
                <label className='custom-file-label' htmlFor='customFile'>
                {filename} <i className='bi bi-file-earmark-pdf-fill'></i>
                </label>
            </div> <hr/>

            <LinearProgress variant="determinate" value={uploadPercentage} />
            <ProgressBar animated variant="success" now={uploadPercentage} label={`${uploadPercentage}%`}/><br/>
            <div className="d-grid gap-5">
                <Fab onClick={handleSubmit} variant="extended" size="medium" color="secondary" aria-label="add">
                    Upload
                    {showSpinner? <Spinner as="span" animation="border" variant="dark"/>:null}
                </Fab>
            </div>
            
            </form>
        </div>
    </>
  );
};

const uploadStyle = {
  color: 'black',
  padding: "2rem",
  fontFamily: 'Calibri light',
  fontSize:'1rem'
};

export default UploadFile;
