import Table from 'react-bootstrap/Table'
import Modal from 'react-bootstrap/Modal'
import Button from '@mui/material/Button';

const AppTableModal = ({show, onHide, features, items}) => {

    const renderItem = (item, index) => {
        //items = [item1, item2, ..., itemk]
        return(
          <tr key={index}>
            {item.map((itemProps, index) => <th key={index}> {itemProps}</th>)}
          </tr>
        )
      }

    return (
        <div>
            <Modal 
                show={show} 
                onHide={onHide}
                style={modalStyle}
                size="sm" 
                centered
                >
                    <Modal.Header>
                        <Modal.Title> התחברות </Modal.Title>
                    </Modal.Header>
                    <Modal.Body style={{textAlign:'center'}} >

                        <Table className="table" bordered hover size="sm" variant="dark">
                            <thead>
                                <tr>
                                    {features.map((feature, index) => <th key={index}> {feature}</th>)}
                                </tr>
                            </thead>
                            <tbody>
                                {items.map(renderItem)}
                            </tbody>
                        </Table>


                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={onHide}>
                        סגור
                        </Button>
                    </Modal.Footer>
            </Modal>
        </div>
    )
}

const modalStyle = {
    textAlign: 'right',
    padding: "80px",
    fontFamily: 'Calibri light',
    fontSize:'18px'
  };

export default AppTableModal
