import React from 'react'
import { useState } from 'react'
import AppSwitch from'./AppSwitch'
import UploadFile from './UploadFile'
import AppTableModal from './AppTableModal'
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import Fab from '@mui/material/Fab';
import InfoRoundedIcon from '@mui/icons-material/InfoRounded';

const HandleFile = ({handleFileSubmit, handleSwitch}) => {
    const [showTextModalTable, setShowTextModalTable] = React.useState(false)
    const [showFeaturesModalTable, setShowFeaturesModalTable] = React.useState(false)

    return (
        <Container maxWidth='md' sx={{width: "90%"}}>
            <p style={{fontFamily: "Calibri light", fontSize: "1.5rem", color: "#bdbdbd", paddingLeft: "0.5rem", textDecoration: "underline"}}>
                Choose Classifyier Type
            </p>
            <AppSwitch 
                leftSwitchText={Text}
                rightSwitchText={Features}
                onSwitch={handleSwitch}
                firstColor={firstColor}
                secondColor={secondColor}
            />
            
            <div style={{color: themeColor, fontSize: '1.3rem'}}>
                <hr/>
                Upload Excel file so that each line is in the following format:
                <br/>
                {classifyingType === Features?
                <>
                    Features (Numbers) | Class (Numbers or Strings)
                    <br/><br/>
                    <Fab 
                        style={{color: "inherit", backgroundColor: "inherit"}}
                        onClick={() => setShowFeaturesModalTable(true)}
                        size="small" 
                        variant="extended" color={themeColor}>
                    <InfoRoundedIcon sx={{ mr: 1 }} />
                    Show Example
                    </Fab>

                    <AppTableModal 
                        show={showFeaturesModalTable}
                        onHide={() => setShowFeaturesModalTable(false)} 
                        title={"File upload example for features classification"}
                        features={["Feature 1", "Feature 2", "Feature 3" , "...", "Class"]} 
                        items={[["0", "1", "0", "...", "0"]]}
                    />
                </>
                :
                <>
                    Text (Numbers) | Class (Numbers or Strings)
                    <br/><br/>
                    <Fab 
                        style={{color: "inherit", backgroundColor: "inherit"}}
                        onClick={() => setShowTextModalTable(true)}
                        size="small" 
                        variant="extended" color={themeColor}>
                    <InfoRoundedIcon sx={{ mr: 1 }} />
                    Show Example
                    </Fab>

                    <AppTableModal 
                        show={showTextModalTable}
                        onHide={() => setShowTextModalTable(false)} 
                        title={"File upload example for text classification"}
                        features={["Text", "Class"]} 
                        items={[["text1", "class1"]]}
                    />
                </>
                }   
                <hr/>
                <UploadFile 
                classifyingType={classifyingType}
                onSubmit={handleFileSubmit}
                />
            </div>
        </Container>
    )
}

export default HandleFile
