import React from 'react'
import Rating from './Rating'
import Container from '@mui/material/Container';

function ScoresAndStats({report}) {
    //const accuracy = (report? report['accuracy'] : 0.0)
    const classesScores = (report? report['classes_scores'] : [])
    const bestFeaturesLabels = (report? report['best_features_labels'] : []) 

    return (
        <Container maxWidth='md' sx={{width: "90%"}}>

                <nobr style={{fontSize: '1.75rem'}}>Model accuracy  </nobr>
                <Rating value={(report['accuracy'] * 5)?? 0}/> {' '}
                <nobr style={{fontSize: '0.75rem', verticalAlign: 'middle'}}>({accuracy})</nobr>
                <hr/>
                * Accuracy may vary from classification to classification.
                <hr/>
                Classes Scores: {classesScores}
                <br/>
                * In case of class with low score {' ->'} more data of this label needed.
                <hr/>
                Best Feature Labels: {bestFeaturesLabels}
            
        </Container>
    )
}

export default ScoresAndStats
