import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';

const HandleTextDataToPredict = ({onSubmit}) => {
    const [textValue, setTextValue] = useState(null)
    const [textValueMessage, setTextValueMessage] = useState("")
    const [textValueError, setTextValueError] = useState(false)

    const [classValue, setClassValue] = useState(null)
    const [classValueMessage, setClassValueMessage] = useState("")
    //const [textValue, setTextValue] = useState(null)
    //const [textValue, setTextValue] = useState(null)


    const handleSubmit = () => {
        if(classValue === null || classValue === "" || textValue === null || textValue === "")
        {
            console.log(textValue)

            setTextValueError(true)
            setTextValueMessage("Empty Text")

            return
        }
    }

    const handleTextChange = e => {
        setTextValue(e.target.value)
    }

    const handleClassChange = e => {
        setClassValue(e.target.value)
    }

    return (
        <Container maxWidth='md' sx={{width: "90%"}}>
            <Box
                component="form"
                noValidate
                autoComplete="off"
                style={{color: "white"}}
                >
                <Grid container spacing={2}>
                    <Grid item xs={8}>
                        <TextField
                            error = {textValueError}
                            fullWidth 
                            label="Text"
                            required
                            helperText={textValueMessage}
                            onChange={handleTextChange}
                        />
                    </Grid>
                    <Grid item xs={4}>
                        <TextField
                            fullWidth 
                            required
                            label="Class"
                            onChange={handleClassChange}
                        />
                    </Grid>
                </Grid>
                <Button onClick={handleSubmit}>click</Button>
            </Box>
        </Container>
    );
}

export default HandleTextDataToPredict
